import React from "react";
import {
  useEffect,
  useState,
  experimental_useEffectEvent as useEffectEvent,
} from "react";
export default function Refecto({ data }) {
  const [pData, setPdata] = useState();
  function isJSON(str) {
    try {
      if (typeof JSON.parse(str) === "number") {
        return false;
      }

      return true;
    } catch (err) {
      return false;
    }
  }

  const recur = useEffectEvent((data) => {
    let parsedData = {};
    for (let key in data) {
      if (typeof data[key] === "object") {
        if (Array.isArray(data[key])) {
          let parsedValue = [];
          for (let i = 0; i < data[key].length; i++) {
            parsedValue.push(recur(data[key][i]));
          }
          parsedData[key] = parsedValue;
        } else {
          const parsedValue = recur(data[key]);
          parsedData[key] = parsedValue;
        }
      } else {
        if (isJSON(data[key])) {
          const parsedValue = recur(JSON.parse(data[key]));
          parsedData[key] = parsedValue;
        } else {
          parsedData[key] = data[key];
        }
      }
    }
    return parsedData;
  });

  useEffect(() => {
    setPdata(recur(data));
  }, [data, recur]);

  return (
    <div className="code-block">
      <pre>{JSON.stringify(pData, null, 2)}</pre>
    </div>
  );
}
