import React from "react";
import { useEffect, useState } from "react";
export default function Refecto({ data }) {
  const [pData, setPdata] = useState();
  function isJSON(str) {
    try {
      if (typeof JSON.parse(str) === "number") {
        return false;
      }

      return true;
    } catch (err) {
      return false;
    }
  }

  const recur = (data) => {
    let parsedValue;
    for (let key in data) {
      if (typeof data[key] === "object") {
        if (Array.isArray(data[key])) {
          for (let i = 0; i < data[key].length; i++) {
            parsedValue = [];
            parsedValue.push(recur(data[i]));
          }
        } else {
          parsedValue = {};
          for (let key in data) {
            // Recursively parse each value and assign it to the corresponding key in parsedValue
            parsedValue[key] = recur(data[key]);
          }
          //const parsedValue = recur(data[key]);
          //parsedObject[key] = parsedValue;
        }
      } else {
        if (isJSON(data[key])) {
          parsedValue = {};
          for (let key in input) {
            // Recursively parse each value and assign it to the corresponding key in parsedValue
            parsedValue[key] = recur(data[key]);
          }
          //const parsedValue = recur(JSON.parse(data[key]));
          //parsedObject[key] = parsedValue;
        } else {
          parsedValue = {};
          parsedValue[key] = data[key];
        }
      }
    }
    return parsedValue;
  };

  useEffect(() => {
    setPdata(recur(data));
  }, [data]);

  return (
    <div className="code-block">
      <pre>{JSON.stringify(pData, null, 2)}</pre>
    </div>
  );
}
